<?php

$settings = [];
$MxSrvsVersion = 'Undefined';
$aliases = [];
$nginxVersion = '';
$mysqlVersion = '';
$phpVersion = phpversion();

$settingsPath = '/Users/xiekecheng/Library/Application Support/MxSrvs/Settings';
if (file_exists($settingsPath)) {
	$settings = json_decode(file_get_contents($settingsPath), true);
	isset($settings['api']['version']) && $MxSrvsVersion = $settings['api']['version'];
	isset($settings['api']['aliases']) && $aliases = $settings['api']['aliases'];
	isset($settings['version']['nginx']) && $nginxVersion = $settings['version']['nginx'];
	isset($settings['version']['mysql']) && $mysqlVersion = $settings['version']['mysql'];
}

// images
$pngFolder = <<< EOFILE

EOFILE;
$pngFolderGo = <<< EOFILE

EOFILE;
$pngFile = <<< EOFILE

EOFILE;
$pngLogo = <<< EOFILE

EOFILE;
$pngPlugin = <<< EOFILE

EOFILE;
$pngWrench = <<< EOFILE

EOFILE;
$favicon = <<< EOFILE

EOFILE;

if (isset($_GET['phpinfo'])) {
	phpinfo();
	exit();
}

if (isset($_GET['img'])) {
	switch ($_GET['img']) {
		case 'pngFolder' :
			header("Content-type: image/png");
			echo base64_decode($pngFolder);
			exit();

		case 'pngFile' :
			header("Content-type: image/png");
			echo base64_decode($pngFile);
			exit();

		case 'pngFolderGo' :
			header("Content-type: image/png");
			echo base64_decode($pngFolderGo);
			exit();

		case 'pngLogo' :
			header("Content-type: image/png");
			echo base64_decode($pngLogo);
			exit();

		case 'pngPlugin' :
			header("Content-type: image/png");
			echo base64_decode($pngPlugin);
			exit();

		case 'pngWrench' :
			header("Content-type: image/png");
			echo base64_decode($pngWrench);
			exit();

		case 'favicon' :
			header("Content-type: image/x-icon");
			echo base64_decode($favicon);
			exit();
	}
}

$projectContents;
$fileContents;
$projectsListIgnore = array('.', '..');
$handle = opendir(".");
while ($file = readdir($handle)) {
	if (!in_array($file, $projectsListIgnore)) {
		if (is_dir($file)) {
			$str_first = substr($file, 0, 1);
			if ($str_first != '@' && $str_first != '_') {
				$projectContents .= '<li><a href="' . $file . '" target="_blank">' . $file . '</a></li>';
			}
		} else {
			$extend = pathinfo($file);
			$extend = strtolower($extend["extension"]);
			$arr_extend = array('html', 'htm', 'php', 'jsp');
			if (in_array($extend, $arr_extend) && $file != "dummy.php" && $file != "index.php")
				$fileContents .= '<li><a href="' . $file . '" target="_blank">' . $file . '</a></li>';
		}
	}
}
closedir($handle);
if (!isset($projectContents)){
	$projectContents = 'No Project';
}

if (!isset($fileContents)) {
	$fileContents = 'No File';
}

$aliasContents;
foreach ($aliases as $key => $val) {
	$aliasContents .= '<li><a href="http://' . $val . '/" target="_blank">' . $key . '</a></li>';
}

if (!isset($aliasContents)) {
	$aliasContents = 'No Alias';
}

$phpExtContents;
$loaded_extensions = get_loaded_extensions();
foreach ($loaded_extensions as $extension) {
	$phpExtContents .= "<li>${extension}</li>";
}

